  it('renders a div', () => {
    const wrapper = mount(Foo, {
      stubs: {
        Bar: '',
        BarFoo: true,
        FooBar: Faz
      }
    })
    expect(wrapper.contains('.stubbed')).toBe(true)
    expect(wrapper.contains(Bar)).toBe(true)
  })
})


